const Header = ({ name }) => <h1>{name}</h1>;

const Total = ({ sum }) => <p>Total of {sum} courses</p>

const Part = ({ part }) =>
    < p >
        {part.name} {part.exercises}
    </p >

const Content = ({ parts }) => {
    return (
        <>
            {parts.map(p => {
                console.log(['p'], p)
                return p.map(obj => {
                    console.log(['obj'], obj);
                    return <Part key={obj.id} part={obj} />
                })
            })}
        </>
    )
}

const Course = ({ courses }) => {
    const parts = courses.map(c => c.parts);
    console.log(['courses'], courses);

    // const sum = parts.reduce((total, num) => total + num.exercises, 0)

    return (
        <>
            {courses.map(course => {
                return (
                    <Header key={course.id} name={course.name} />
                )
            })}
            <Content parts={parts} />
            {/* <Total sum={sum} /> */}
        </>
    )
}

const App = () => {
    const courses = [
        {
            name: 'Half Stack application development',
            id: 1,
            parts: [
                {
                    name: 'Fundamentals of React',
                    exercises: 10,
                    id: 1
                },
                {
                    name: 'Using props to pass data',
                    exercises: 7,
                    id: 2
                },
                {
                    name: 'State of a component',
                    exercises: 14,
                    id: 3
                },
                {
                    name: 'Redux',
                    exercises: 11,
                    id: 4
                }
            ]
        },
        {
            name: 'Node.js',
            id: 2,
            parts: [
                {
                    name: 'Routing',
                    exercises: 3,
                    id: 1
                },
                {
                    name: 'Middlewares',
                    exercises: 7,
                    id: 2
                }
            ]
        }
    ]

    return <Course courses={courses} />
}

export default App
